var bitcoin = require('bitcoinjs-lib');

function signTx (unsignedTx, privateKey) {
  var tx = bitcoin.Transaction.fromHex(unsignedTx)
  var insLength = tx.ins.length
  for (var i = 0; i < insLength; i++) {
    tx.sign(i, privateKey)
  }
  return tx.toHex()
}

